import React ,{useState}from 'react'
import Cookies from 'universal-cookie'
import axios from 'axios'

import signiniImage from '../assets/signup.jpg'


const Auth = () => {

    const [isSignup,setIsSignup]=useState(true)
const handleChange=()=>{}
const switchMode=()=>{
    setIsSignup((prevIsSignup)=>!prevIsSignup);
}

  return (
   <div className="auth__from-container">
    <div className="auth__form-container_fields">
        <div className="auth__form-container_fields-content">
            <p>{isSignup?'signup ':'sign in'}</p>
            <form action="" onSubmit={""}>
                {isSignup && ( 
                    <div className="auth__form-container_fields-content_input">
                        <label htmlFor="fullName"> Full Name</label>
                        <input type="text" 
                        name="fullName"
                        placeholder='Full Name'
                        onChange={handleChange}
                        required
                        />
                    </div>
                )   }
                 <div className="auth__form-container_fields-content_input">
                        <label htmlFor="username"> User Name</label>
                        <input type="text" 
                        name="username"
                        placeholder='User xName'
                        onChange={handleChange}
                        required
                        />
                    </div>
                    {isSignup && ( 
                    <div className="auth__form-container_fields-content_input">
                        <label htmlFor="phonenumber"> Phone Number</label>
                        <input type="text" 
                        name="phoneNumber"
                        placeholder='Phone Number '
                        onChange={handleChange}
                        required
                        />
                    </div>
                )   }
                  {isSignup && ( 
                    <div className="auth__form-container_fields-content_input">
                        <label htmlFor="avaterURL"> Avatar URL</label>
                        <input type="text" 
                        name="avatarURL"
                        placeholder='Avatar URL '
                        onChange={handleChange}
                        required
                        />
                    </div>
                )   }
                  {isSignup && ( 
                    <div className="auth__form-container_fields-content_input">
                        <label htmlFor="password"> Password</label>
                        <input type="password" 
                        name="password"
                        placeholder='Password'
                        onChange={handleChange}
                        required
                        />
                    </div>
                )   }
                  {isSignup && ( 
                    <div className="auth__form-container_fields-content_input">
                        <label htmlFor="confimpassword"> Confim Password</label>
                        <input type="password" 
                        name="confirmpassword"
                        placeholder='Confirm password'
                        onChange={handleChange}
                        required
                        />
                    </div>
                )   }
            </form>
            {isSignup && ( 
                    <div className="auth__form-container_fields-content_input">
                       <p>
                        {
                            isSignup?"already have an account " :"don't have an account? "
                        }
                        <span onClick={switchMode}>
                            {isSignup ?"Sign In ":"sign Up "}

                        </span>
                      </p>
                    </div>
                )   }
        </div>
    </div>
   </div>
  )
}

export default Auth
